function [x1, x2, x3, x4, x5, N] = create_audio(str, n, delta)% On charge la piste de batterie et on calcule le diagramme temps-fr�quence[y,] = audioread(strcat('../Data/essai',str,'/instrument01.wav'));y1 = y(:,1);N = length(y);x1 = diagrammeTF(y1, N, n, delta);% On charge la piste de la m�lodie et on calcule le diagramme temps-fr�quence[y,] = audioread(strcat('../Data/essai',str,'/instrument02.wav'));y2 = y(:,1);x2 = diagrammeTF(y2, N, n, delta);% On charge la piste de la basse et on calcule le diagramme temps-fr�quence[y,] = audioread(strcat('../Data/essai',str,'/instrument03.wav'));y3 = y(:,1);x3 = diagrammeTF(y3, N, n, delta);% On charge la piste de l'accompagnement et on calcule le diagramme temps-fr�quence[y,] = audioread(strcat('../Data/essai',str,'/instrument04.wav'));y4 = y(:,1);x4 = diagrammeTF(y4, N, n, delta);y5 = zeros(size(y4));for i=1:min(min(length(y5),length(y3)),min(length(y2),length(y1)))  y5(i) = (0.5+0.5*sin(40*delta*i))*y1(i) + (0.5+0.5*sin(80*delta*i))*y2(i) + (0.5+0.5*cos(40*delta*i))*y3(i) + 0.4*y4(i);end;x1 = abs(x1);x2 = abs(x2);x3 = abs(x3);x4 = abs(x4);x5 = abs(diagrammeTF(y5, N, n, delta));return;end