function [x, compteur, conv]=multiplication_resolution3(V, W, H, beta, epsilon)compteur = 1;%PB : quand il y a des cases nulles dans la WH ou V on a ensuite NaN%solution possible, ajouter Ã  V et Ã  chaque colonne de W*H un epsilon%en fonction de la valeur prÃ©cÃ©dentelim = 10000;Hprim = H + 2*epsilon*ones(size(H));Hsec = Hprim;conv = zeros([length(H) 10000]);Vprim = W*H;conv(:,1) = H;Vprim = W*H;while abs(sum(Hprim-H))>epsilon    for i=1:length(H)       col = W(:,i);       Hprim = H;       A = transpose(col)*(((col*H(i)).^(beta-2)).*V);       B = transpose(col)*((col*H(i)).^(beta-1));       if A>B        x = B/A;       else        x=A/B;       end;              H(i) = H(i)*x;              Vprim = W*H;       compteur = compteur + 1;    end;        conv(:,compteur) = H;    if compteur >= lim        break;    end;end; x = H;return;end