function [H, H1, compteur1, H2, compteur2, conv1, conv2] = testNMF(H, beta, epsilon)piste_do = audioread('../Data/testNMF/do.wav');piste_sol = audioread('../Data/testNMF/sol.wav');y_sol = transpose(fft_morceau(piste_sol(1:1024)));y_do = transpose(fft_morceau(piste_do(1:1024)));W = horzcat(y_do, y_sol);Wabs = abs(W);V1 = Wabs*H;V2 = abs(W*H);[H1, compteur1 , conv1] = multiplication_resolution2(V1, Wabs, [10;10], beta, epsilon);[H2, compteur2, conv2] = multiplication_resolution2(V2, Wabs, [10;10], beta, epsilon);return;end